// eslint-disable-next-line
import * as THREE from 'three'
import React, { useRef, useState, Suspense } from 'react'
import { Canvas, useFrame, useLoader } from '@react-three/fiber'
import { Bvh, Instances, Instance, Text, Clone, OrbitControls, Environment, useGLTF, Html } from '@react-three/drei'
import { DRACOLoader } from 'three/examples/jsm/loaders/DRACOLoader.js'
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader.js'
import { useControls } from 'leva'
import Model from './Model'


const positions = [
  { position: [0, -12.8, 0] },
  { position: [0, -6.4, 0] },
  { position: [0, 0, 0] },
  { position: [0, 6.4, 0] },
  { position: [0, 12.8, 0] }
];

const App = () => {
  const predefinedPositions = [
    [0, 0, 0],
    [12, 0, 0],
    [-12, 0, 0],
    [0, 12, 0],
    [0, -12, 0],
  ];

  const [models, setModels] = useState([]);

  const addModel = (position) => {
    setModels((prevModels) => [...prevModels, <Model key={Date.now()} position={position} />]);
  };

  return (
    <Canvas
      shadowMap
      camera={{ position: [0, 5, 10], fov: 60 }}
    >
      <ambientLight intensity={0.5} />
      <spotLight position={[5, 5, 5]} angle={0.3} penumbra={1} castShadow />
      <Suspense fallback={null}>
        {models.map((model, index) => (
          <React.Fragment key={index}>{model}</React.Fragment>
        ))}
      </Suspense>
      <OrbitControls />
      <Html>
      {predefinedPositions.map((position, index) => (
        <button key={index} onClick={() => addModel(position)}>
          Add Model at Position {position.toString()}
        </button>
      ))}
      </Html>
    </Canvas>
  );
};

export default App;
