// eslint-disable-next-line
import * as THREE from 'three'
import React, { useRef, useState, Suspense } from 'react'
import { Canvas, useFrame, useLoader } from '@react-three/fiber'
import { Bvh, Instances, Instance, Text, Clone, OrbitControls, Environment, useGLTF, Html } from '@react-three/drei'
import { DRACOLoader } from 'three/examples/jsm/loaders/DRACOLoader.js'
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader.js'
import { useControls } from 'leva'
import Model from './Model'



export default function App() {
  const positions = [
    { position: [0, -12.8, 0] },
    { position: [0, -6.4, 0] },
    { position: [0, 0, 0] },
    { position: [0, 6.4, 0] },
    { position: [0, 12.8, 0] }
  ];

  const bldg = useGLTF('/panelEdited.glb')
  const [model, setModel] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const { range } = useControls({ range: { value: 1, min: 1, max: 5, step: 1 } })
  const addModel = () => {
    if (currentIndex < positions.length) {
      const currentPosition = positions[currentIndex];
    setModel((i) => [...model,
      <Clone
      key={i}
      object={bldg.scene}
      scale={0.069}
      position={currentPosition}
      castShadows
      />
    ])
    console.log(model)
    }
  }
  return (
      <Canvas camera={{ position: [0, 0, 20], fov: 50 }}>
        <ambientLight intensity={0.5 * Math.PI} />
        <directionalLight intensity={0.3} position={[5, 25, 20]} />
        <Bvh firstHitOnly>
          <Suspense fallback={<Placeholder/>}>
            {/* <Models range={range}/> */}
            {model.map((mod, i) => (
              <Clone
              key={i}
              object={bldg.scene}
              scale={0.069}
              position={mod.position}
              castShadows
              />
            ))}
          </Suspense>
        </Bvh>
        <Html occlude distanceFactor={1.5} position={[-25, 0, 0]} transform>
        {/* <span>Size</span> */}
          <button
            style={{ width: 700, height: 400, background:"teal", color: 'white', fontSize: 150 }}
            /*onChange={(value) => ((controls.enabled = false), set(value))}
            onAfterChange={() => (controls.enabled = true)} */
            /* onClick={() => addModel()} */
            onClick={() => addModel()}
          >
          Add floor
          </button>
      </Html>

        <Environment preset="city" />
        <OrbitControls autoRotateSpeed={1} />
      </Canvas>
  )
}

const Models = ({ range }) => {
  const bldg = useGLTF('/panelEdited.glb')
  const [model, setModel] = useState(positions);

  const addModel = (position) => {
    setModel((prevModels) => [...prevModels, <Model key={Date.now()} position={position} />]);
  };

  return (
    <Instances range={range}>
      <boxGeometry />
      <meshStandardMaterial />
      {addModel.map((mod) => (
        <Clone
        key={mod.position}
        object={bldg.scene}
        scale={0.069}
        position={mod.position}
        castShadow
        />
      ))}
    </Instances>
  )
}

const Placeholder = (props) =>
{
    return <mesh position-y={ 0.5 } scale={ [ 2, 3, 2 ] } {...props}>
        <boxGeometry args={ [ 1, 1, 1, 2, 2, 2 ] } />
        <meshBasicMaterial wireframe color="red" />
    </mesh>
}
