import * as THREE from 'three'
import { useRef, useState } from 'react'
import { Canvas, useFrame } from '@react-three/fiber'
import { Bvh, Instances, Instance, OrbitControls, Environment, useGLTF } from '@react-three/drei'
import { useControls } from 'leva'

/* import { data } from './store' */

const position = (r) => [1, r + 1 - Math.random(), 1]
const data = Array.from({ length: 10 }, (r = 10) => ({ random: Math.random(), position: position(r)}))


export default function App() {
  const { range } = useControls({ range: { value: 10, min: 1, max: 10, step: 1 } })
  return (
    <Canvas camera={{ position: [0, 0, 20], fov: 50 }}>
      <ambientLight intensity={0.5 * Math.PI} />
      <directionalLight intensity={0.3} position={[5, 25, 20]} />
      <Bvh firstHitOnly>
        <Shoes data={data} range={range} />
      </Bvh>
      <Environment preset="city" />
      <OrbitControls /* autoRotate */ autoRotateSpeed={0}/>
    </Canvas>
  )
}

function Shoes({ data, range }) {
  const { nodes, materials } = useGLTF('/shoe.glb')
  /* console.log() */
  return (
    <Instances range={range} material={materials.phong1SG} geometry={nodes.Shoe.geometry}>
      {data.map((props, i) => (
        <Shoe key={i} {...props} />
      ))}
    </Instances>
  )
}

function Shoe({ /* random, color = new THREE.Color(), */ ...props }) {
  const ref = useRef()
