// eslint-disable-next-line
import * as THREE from 'three'
import React, { useRef, useState, Suspense } from 'react'
import { Canvas, useFrame, useLoader, useThree } from '@react-three/fiber'
import { Bvh, Instances, Instance, Text, Clone, OrbitControls, Environment, useGLTF, Html } from '@react-three/drei'
import { DRACOLoader } from 'three/examples/jsm/loaders/DRACOLoader.js'
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader.js'
import { useControls } from 'leva'
import { Slider } from 'antd'
import Model from './Model'

const positions = [
  { position: [0, -12.8, 0] },
  { position: [0, -6.4, 0] },
  { position: [0, 0, 0] },
  { position: [0, 6.4, 0] },
  { position: [0, 12.8, 0] }
];

export default function App() {
  const bldg = useGLTF('/panelEdited.glb')
  const [myRange, set] = useState(5)
  const [model, setModel] = useState([]);
  const [flr, setFlr] = useState(1);
  const { range } = useControls({ range: { value: 1, min: 1, max: 5, step: 1 } })
  const addModel = () => {
    const r = 6.4
    setModel((i, position) => [...model,
    <Clone
      key={i}
      object={bldg.scene}
      scale={0.069}
      position={position}
      castShadows
      />
    ])
    setFlr(model.length)
    console.log(model)
    }

    const Models = ({ range }) => {
      const controls = useThree((state) => state.controls)
      return (
        <Instances range={positions}>
          <boxGeometry />
          <meshStandardMaterial />
          {model.map((mod) => (
            <Clone
            key={mod.position}
            object={bldg.scene}
            scale={0.069}
            position={mod.position}
            castShadow
            />
          ))}
        </Instances>
      )
    }

  return (
      <Canvas camera={{ position: [0, 0, 20], fov: 50 }}>
        <ambientLight intensity={0.5 * Math.PI} />
        <directionalLight intensity={0.3} position={[5, 25, 20]} />
        <Bvh firstHitOnly>
          <Suspense fallback={<Placeholder/>}>
            <Models/>
          {/* <Instances range={myRange}>
            <boxGeometry />
            <meshStandardMaterial />
            {positions.map((mod) => (
              <Clone
              key={mod.position}
              object={bldg.scene}
              scale={0.069}
              position={mod.position}
              castShadow
              />
            ))}
          </Instances> */}
          </Suspense>
        </Bvh>
        <Html occlude distanceFactor={1.5} position={[-10, 0, 0]} transform>
        {/* <span>Size</span> */}
          {/* <button
            style={{ width: 700, height: 400, background:"teal", color: 'white', fontSize: 150 }}
            nChange={(value) => ((controls.enabled = false), set(value))}
            onAfterChange={() => (controls.enabled = true)}
            onClick={() => addModel()}
            onClick={() => addModel()}
          >
          Add floor
          </button> */}
          <Slider
          min={1}
          max={5}
          step={1}
          value={myRange}
          style={{ width: 700, height: 100, background:"teal", color: 'white', fontSize: 150 }}
          onChange={(value) => ((controls.enabled = false), set(value))}
          onAfterChange={() => (controls.enabled = true)}
        />
          {/* <input style={{ width: "20", background:"teal", color: 'white' }} type="range" min="1" max="5" value={myRange} onChange={(value) => (set(value))} className="slider" id="myRange"/> */}
      </Html>

        <Environment preset="city" />
        <OrbitControls autoRotateSpeed={1} />
      </Canvas>
  )
}



